import { Radar } from "react-chartjs-2"
import React, { useState, useRef } from "react";
import { CircleSlider } from "react-circle-slider";

const RadarChart = ({ chartData }) => {
    const DataforRadarChart = [
        {
            title: "Movement",
            balanceScore: 150
        },
        {
            title: "Sleep",
            balanceScore: 50
        },
        {
            title: "Food",
            balanceScore: 125
        },
        {
            title: "Occupation",
            balanceScore: 300
        },
        {
            title: "Activity",
            balanceScore: 100
        },
    ]

    const [radarChartData, setRadarChartData] = useState({
        labels: DataforRadarChart.map(data => data.title),
        datasets: [
            {
                label: "Balance Score",
                data: DataforRadarChart.map(data => data.balanceScore),
                backgroundColor: [
                    "#31356e",
                    "#6ce5e8",
                    "#41b8d5",
                    "#2f5f98 ",
                    "#704e85"

                ],
                borderColor: "white",
                borderWidth: 1
            }
        ]

    })

    const [value, setChangeValue] = useState(200);
    const slider = useRef(null);




    return ( <>
        <div>
            <h2 style={{ textAlign: "center" }}>Radar Chart</h2>
            <Radar
                data={radarChartData}
                options={{

                        scales: {
                            r: {
                                ticks: {
                                    color: "#6ce5e8",
                                    font: 1,
                                    showLabelBackdrop: false
                                }
                            }
                        }

                }}
            />
        </div>
         <h2>Circluar Slider</h2>
         <div className='AppCircle'>
       <div className="textContainer">
         {value}
         <div className="minute">MINUTES</div>
       </div>
         <CircleSlider
           ref={slider}
           value={value}
           stepSize={1}
           onChange={value => setChangeValue(value)}
           size={250}
           max={501}
           gradientColorFrom="#6ce5e8"
           gradientColorTo="#2f5f98"
           knobRadius={15}
           circleWidth={20}
            disabled={true}
         />
         </div>
         <input type="number" onChange={(event) => setChangeValue(event.target.value)}></input>
         </> )
}

export default RadarChart
