import { Radar } from "react-chartjs-2"

const RadarChart = ({ chartData }) => {
    const DataforRadarChart = [
        {
            title: "Movement",
            balanceScore: 150
          },
          {
            title: "Sleep",
            balanceScore: 50
          }, 
          {
            title: "Food",
            balanceScore: 125
          },
          {
            title: "Occupation",
            balanceScore: 300
          },
          {
            title: "Activity",
            balanceScore: 100
          },
    ]

    const [radarChartData, setPieChartData] = useState({
        labels: DataforPieChart.map(data => data.title),
        datasets: [
          {
            label: "Balance Score",
            data: DataforPieChart.map(data => data.balanceScore),
            backgroundColor: [
              "#31356e",
              "#6ce5e8",
              "#41b8d5",
              "#2f5f98 ",
              "#704e85"

            ],
            borderColor: "white",
            borderWidth: 1
          }
        ]

      })





    return (
        <div>
            <h2 style={{ textAlign: "center" }}>Radar Chart</h2>
            <Radar
                data={chartData}
                options={{
                    plugins: {
                        scales: {
                            y: {
                                ticks: {
                                    display: false
                                }
                            }
                        }
                    }
                }}
            />
        </div>
    )
}

export default RadarChart
